insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (18, 1, 3, 1.17, -1.456, 1.456, null, 'Elbow 0° wrist stability', null, '請你將手掌抬高至大拇指與前臂成一直線(手腕伸直約15度)，我要給你一個阻力，不要被我壓下去|Dorsiflex (extend) the wrist to the full range of 15° (or full available range) with the elbow fully extended and the shoulder at 30° flexion. If full range of dorsiflexion is attained, slight resistance is given', '肩關節稍微屈曲及外展；前臂旋前，施測者可幫忙支撐前臂|Sitting with elbow extended and forearm pronated', '0 無法抬高至15度|0 Cannot dorsiflex wrist to required 15° ', '1 可抬高15度但不能抗阻力|1 Dorsiflexion is accomplished, but no resistance is taken', '2 可抬高15度亦可抗阻力|2 Dorsiflexion can be maintained with some (slight) resistance ', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (10, 1, 3, -0.389, -0.125, 0.125, null, 'Hand to lumbar spine', null, '請將手背放至腰椎|Put your affected hand behind your back', '屈曲協同動作，施測者可幫忙支撐|Sitting with hand resting on lap', '0 無法執行|0 No active motion', '1 部分角度|1 Partial motion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (19, 1, 3, 0.045, -1.594, 1.594, null, 'Elbow 0° wrist flexion/extension', null, '請你將手掌往上抬至最高，然後放至最低(施測者同時測量病患PROM)，並重複做3次|Repeated (3 times) smooth alternating movements from 15 degrees of dorsiflexion (wrist extension) to 15 degrees of volar flexion with the elbow fully extended and the shoulder at 30° flex', '肩關節稍微屈曲及外展；前臂旋前，施測者可幫忙支撐前臂|Sitting with elbow extended and forearm pronated', '0 無法執行|0 No active motion', '1 部分角度|1 Partial motion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (20, 1, 3, 1.831, -1.669, 1.669, null, 'Circumduction (wrist)', null, '請你兩手同時繞圈|Circumduct the wrist with smooth alternating movements throughout the full range of circumduction', '手肘屈曲90°、前臂旋前，施測者可幫忙支撐前臂|Sitting with arm at side elbow flexed to 90°, and forearm pronated', '0 無法做出動作|0 No active motion', '1 動作不順暢|1 Jerky motion or incomplete circumduction', '2 與健側手動作差不多|2 Complete motion with smoothness', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (21, 1, 3, -0.075, -1.664, 1.664, null, 'Hand, mass extension', null, '請用力打開手掌，手指伸直|Fully extend all fingers', '手肘屈曲90度，前臂擺在正中位置，施測者可幫忙支撐|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法執行|0 No active motion', '1 部分角度|1 Partial motion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (22, 1, 3, 2.298, 0.574, -0.574, null, 'Hook grasp', null, '請慢慢將手指彎曲成貓爪樣子，我要給你阻力，手指不要被我扳動|Pretend you are holding the handle of a briefcase. Test this grip against resistance.', '手肘屈曲90度，前臂擺在正中位置，施測者可幫忙支撐，手指伸直|Sitting with arm on bedside table or lap, finger extended', '0 無法做到動作|0 No active motion', '1 可做出動作但無法抗阻力|1 Grasp is weak', '2 可做出動作亦能抗阻力|2 Grasp can be maintained against relatively great resistance', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (23, 1, 3, 2.116, -0.726, 0.726, null, 'Lateral prehension', null, '請用大拇指與食指夾住這張紙，我要輕拉紙張，不要被我拉走|Perform pure thumb adduction with the scrap of paper interposed between the thumb and first digit. Test this grip against resistance by asking the patient to hold as you attempt to pull the aper out with a slight tug.', '手肘屈曲90度，前臂旋前，施測者可幫忙支撐，並準備一張紙|Sitting with arm on bedside table. Prepare a piece of paper.', '0 無法做到動作|0 No active motion', '1 可做出動作但無法抗阻力|1 Thumb and index finger can be kept in place, but not against a slight tug', '2 可做出動作亦能抗阻力|2 Paper is held firmly against a tug', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (24, 1, 3, 2.826, -0.923, 0.923, null, 'Palmar prehension', null, '請用大拇指及食指拿住這支筆，我要輕拉筆，不要被我拉走|Grasp a pen by opposing the thumb and index finger pads around the pen. Test this grip against resistance by asking the patient to hold as you attempt to pull the pencil out with a slight tug.', '手肘屈曲90度，前臂擺在正中位置，施測者可幫忙支撐，並準備一支筆|Sitting with arm on bedside table. Prepare a pen.', '0 無法做到動作|0 No active motion', '1 可做出動作但無法抗阻力|1 Thumb pad and pad of the index finger can be kept in place, but not against a slight tug', '2 可做出動作亦能抗阻力|2 Pencil is held firmly against a tug', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (25, 1, 3, 1.93, -1.326, 1.326, null, 'Cylinder grip', null, '請用大拇指與其他四指其中一指握住這個杯子，我要輕拉杯子，不要被我拉走|Grasp a small can (placed upright on a table without stabilization) by opening the fingers and opposing the volar surfaces of the thumb and digits. Test this grip against resistance by asking the patient to hold as you attempt to pull the can out with a slight tug. ', '手肘屈曲90度，前臂擺在正中位置，施測者可幫忙支撐，並準備一直徑8公分的杯子|Sitting with arm on bedside table. Prepare a can with 8 cm in diameter.', '0 無法做到動作|0 No active motion', '1 可做出動作但無法抗阻力|1 Thumb and index finger can be kept in place, but not against a slight tug', '2 可做出動作亦能抗阻力|2 Can is held firmly against a tug ', '');
