insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (26, 1, 3, 2.207, -1.051, 1.051, null, 'Spherical grip', null, '手肘屈曲90度，前臂旋前，施測者可幫忙支撐，並準備一顆球，將球擺在患側手掌下方|Grasp a tennis ball. Test this grip against resistance by asking the patient to hold as you attempt to pull the ball out with a slight tug.', '請將球拿起握緊，我要輕拉球，不要被我拉走|Sitting with arm on bedside table. Prepare a tennis ball.', '0 無法做到動作|0 No active motion', '1 可做出動作但無法抗阻力|1 A tennis ball can be kept in place with a spherical grasp, but not against a slight tug', '2 可做出動作亦能抗阻力|2 Tennis ball is held firmly against a tug ', '');
