insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (6, 1, 3, -2.321, -1.966, 1.966, null, 'Flexor synergy：Forearm supination', 'Flexor synergy：Forearm supination', '請用手去摸你右（左）邊的耳朵後面，像接電話那樣，手心向後<br>*註一：shoulder abduction須≧90度，才能得2分<br>*註二：個案軀幹不可旋轉|Bring the hand to the ear of the affected side and shoulder should be abducted at least 95 degrees', '患側手輕鬆置於患腳大腿上|Sitting, affected limb extend towards opposite knee in shoulder adduction/internal rotation, elbow extension, and forearm pronation.', '0 無法執行|0 Noactivemotion', '1 部分動作，或動作與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (7, 1, 3, -2.367, -1.922, 1.922, null, 'Extensor synergy：Shoulder adduction/internal rotation', 'Extensor synergy：Shoulder adduction/internal rotation', '請用右（左）手拇指去摸到你對側膝蓋的內側。等一下我放開手，你必須自己出力完成<br>*註一：若動作明顯為地心引力作用造成，則為0分<br>*註二：個案軀幹不可旋轉|Adduct & internally rotate the shoulder, extend arm towards the unaffected knee with the forearm pronated', '屈曲協同動作（用患手去摸你患側邊的耳朵後面，像接電話那樣，手心向後）施測者可幫忙支撐|Limb is passively placed at patient’s side in elbow flexion and supination', '0 無法執行|0 Noactivemotion', '1 部分動作，或動作與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (8, 1, 3, -1.922, -1.962, 1.962, null, 'Extensor synergy：Elbow extension', 'Extensor synergy：Elbow extension', '請用右（左）手拇指去摸到你對側膝蓋的內側。等一下我放開手，你必須自己出力完成<br>*註一：若動作明顯為地心引力作用造成，則為1分<br>*註二：個案軀幹不可旋轉|Adduct & internally rotate the shoulder, extend arm towards the unaffected knee with the forearm pronated', '屈曲協同動作（用患手去摸你患側邊的耳朵後面，像接電話那樣，手心向後）施測者可幫忙支撐|Limb is passively placed at patient’s side in elbow flexion and supination', '0 無法執行|0 Noactivemotion', '1 部分動作，或動作與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (9, 1, 3, -1.799, -1.287, 1.287, null, 'Extensor synergy：Forearm pronation', 'Extensor synergy：Forearm pronation', '請用右（左）手拇指去摸到你對側膝蓋的內側。等一下我放開手，你必須自己出力完成<br>*註一：若動作明顯為地心引力作用造成，則為2分<br>*註二：個案軀幹不可旋轉|Adduct & internally rotate the shoulder, extend arm towards the unaffected knee with the forearm pronated', '屈曲協同動作（用患手去摸你患側邊的耳朵後面，像接電話那樣，手心向後）施測者可幫忙支撐|Limb is passively placed at patient’s side in elbow flexion and supination', '0 無法執行|0 Noactivemotion', '1 部分動作，或動作與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (10, 1, 3, -0.389, -0.125, 0.125, null, 'Hand to lumbar spine', 'Hand to lumbar spine', '請將手背放至腰椎<br>*註一：個案軀幹不可旋轉|Put your affected hand behind your back', '患側手輕鬆置於患腳大腿上 背部遠離椅背|Sitting with hand resting on lap', '0 無法執行|0 Noactivemotion', '1 可超過anterior-superioriliacspine(ASIS)|1 Partialmotion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (11, 1, 3, 0.9, -0.167, 0.167, null, 'Shoulder flexion 0° to 90°', 'Shoulder flexion 0° to 90°', '請將手往前伸直舉到水平90度，手心朝內<br>*註一：個案forearm須為正中姿勢(mid position，即手心朝身體中線)，才能得兩分|Flex shoulder to 90°, elbow fully extended, forearm can be in pronation or in a mid-position between pronation and supination', '患側手輕鬆置於患腳大腿上|Sitting with hand resting on lap', '0 無法執行，或一動作就出現ShoulderABD或Elbowflexion的動作|0 Noactivemotion', '1 有手往前伸直的動作，後期才出現ShoulderABD或Elbowflexion的動作|1 Partialmotion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (12, 1, 3, 0.773, -0.189, 0.189, null, 'Elbow 90° pronation/supination', 'Elbow 90° pronation/supination', '請將手肘彎曲90度，手心轉上轉下<br>*註一 ：若個案無法自己達到正確起始姿勢(Elbow flex 90, shoulder 0)，則0分<br>*註二 ：若個案可自己達到正確起始姿勢，過程中評估者可稍微支撐患側手肘|Flex elbow to 90° and pronate/supinate the forearm through the full available ROM', '患側手肘彎曲90度，上臂與身體平行（shoulder 0度），手心向下|Sitting with arm at side, elbow flexed, and forearm in supination', '0 無法執行|0 Noactivemotion', '1 部分動作|1 Partialmotion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (13, 1, 3, 1.864, 0.482, -0.482, null, 'Shoulder abduction 0° to 90°', 'Shoulder abduction 0° to 90°', '請將手側舉到水平，手肘伸直，手心向下|Abduct the shoulder to 90°, elbow extended, forearm pronated', '患側手輕鬆置於身體側，上臂與身體平行  施測者可幫忙支撐|Sitting with arm and hand resting at side', '0 完全無動作，或一動作就出現Forearmsupination或Elbowflexion的動作|0 Noactivemotion', '1 部分動作，且後期才出現Forearmsupination或Elbowflexion的動作|1 Partialmotion', '2 可完成全部動作並達到正確姿勢|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (14, 1, 3, 3.518, -1.758, 1.758, null, 'Shoulder flexion 90° to 180°', 'Shoulder flexion 90° to 180°', '請保持拇指朝上，手肘伸直，將手臂舉至耳朵旁邊，手心朝內<br>*註一：個案forearm須為mid-position(即手心朝身體中線)，才能得2分|Flex shoulder above 90°, with elbow fully extended, forearm in the midposition between pronation and supination', '患側手輕鬆置於患腳大腿上|Sitting with elbow extended, hand resting on knee', '0 無法執行，或一動作就出現ShoulderABD或Elbowflexion的動作|0 Noactivemotion', '1 有手往前伸直的動作，後期才出現ShoulderABD或Elbowflexion的動作|1 Partialmotion', '2 可完成全部動作並達到正確姿勢|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (15, 1, 3, 2.984, -1.02, 1.02, null, 'Elbow 0° pronation/supination', 'Elbow 0° pronation/supination', '請將手肘伸直，稍微向前舉，手心轉上轉下<br>*註一：個案無法自己達到正確起始姿勢，則0分<br>*註二：個案可自己達到正確起始姿勢，過程中評估者可稍微支撐患側手肘|Pronate and supinate the forearm, elbow fully extended, shoulder at 30°-90° of flexion', '施測者不能給予支撐，患側手稍微向前舉（30 – 90度間），手肘伸直|Sitting with elbow extended and shoulder at 30°-90° of flexion', '0 無法執行|0 Noactivemotion', '1 部分角度|1 Partialmotion', '2 完全完成|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (16, 1, 3, 1.033, -1.303, 1.303, null, 'Elbow 90° wrist stability', 'Elbow 90° wrist stability', '請抬高手腕使你的大拇指與前臂成一直線，請出力撐住，不要被我壓下去<br>*註一：如必要，評估者可稍微支撐患側前臂：距離手腕約兩指幅處|Dorsiflex (extend) the wrist to the full range of 15° with the elbow at 90° flexion and the shoulder at 0°. If full range of dorsiflexion is attained, slight resistance is given.', '患側手肘彎曲90度，上臂與身體平行（shoulder 0度），手心向下|Sitting with arm and forearm pronated', '0 完全無法做出動作，或無法抬高手腕到大拇指與前臂成一直線|0 Cannotdorsiflexwristtorequired15°', '1 可達到正確姿勢，但無法抗阻力|1 Dorsiflexionisaccomplishedbutnoresistanceistaken', '2 可完成全部動作及抗阻力|2 Dorsiflexion can be maintained with some (slight) resistance ', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (17, 1, 3, 0.062, -1.634, 1.634, null, 'Elbow 90° wrist flexion/extension', 'Elbow 90° wrist flexion/extension', '請將手腕向內向外彎曲到最大角度，可以慢慢做，來回做三次<br>*註一：如必要，評估者可稍微支撐患側前臂：距離手腕約兩指幅處|Repeated (3 times) smooth alternating movements from 15 degrees of dorsiflexion (wrist extension) to 15 degrees of volar flexion with the fingers somewhat flexed', '患側手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢（即手心朝向身體中線）|Sitting with arm and forearm pronated', '0 無法執行|0 Noactivemotion', '1 部分動作，或可達到最大角度但過程不順暢|1 Partialmotion', '2 可來回達到最大角度且動作順暢|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (18, 1, 3, 1.17, -1.456, 1.456, null, 'Elbow 0° wrist stability', 'Elbow 0° wrist stability', '請你將手掌抬高至大拇指與前臂成一直線(手腕伸直約15度)，我要給你一個阻力，不要被我壓下去<br>*註一：如必要，評估者可稍微支撐患側前臂：距離手腕約兩指幅處|Dorsiflex (extend) the wrist to the full range of 15° (or full available range) with the elbow fully extended and the shoulder at 30° flexion. If full range of dorsiflexion is attained, slight resistance is given', '患側手稍微向前舉（30 – 90度間），手肘伸直，手心向下|Sitting with elbow extended and forearm pronated', '0 完全無法做出動作，或無法抬高手腕到大拇指與前臂成一直線|0 Cannotdorsiflexwristtorequired15°', '1 可達到正確姿勢，但無法抗阻力|1 Dorsiflexionisaccomplishedbutnoresistanceistaken', '2 可完成全部動作及抗阻力|2 Dorsiflexion can be maintained with some (slight) resistance ', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (19, 1, 3, 0.045, -1.594, 1.594, null, 'Elbow 0° wrist flexion/extension', 'Elbow 0° wrist flexion/extension', '請將手腕向內向外彎曲到最大角度，可以慢慢做，來回做三次<br>*註一：如必要，評估者可稍微支撐患側前臂：距離手腕約兩指幅處|Repeated (3 times) smooth alternating movements from 15 degrees of dorsiflexion (wrist extension) to 15 degrees of volar flexion with the elbow fully extended and the shoulder at 30° flex', '患側手稍微向前舉（30 – 90度間），手肘伸直，前臂在正中姿勢（即手心朝向身體中線）|Sitting with elbow extended and forearm pronated', '0 無法執行|0 Noactivemotion', '1 部分動作，或可達到最大角度但過程不順暢|1 Partialmotion', '2 可來回達到最大角度且動作順暢|2 Complete motion with smoothness', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (20, 1, 3, 1.831, -1.669, 1.669, null, 'Circumduction (wrist)', 'Circumduction (wrist)', '請將兩邊手腕同時做出繞圈的動作，慢慢做，做三次<br>*註一：如必要，評估者可稍微支撐患側前臂：距離手腕約兩指幅處<br>*註二：不可利用前臂的supination /pronation的代償動作來完成|Circumduct the wrist with smooth alternating movements throughout the full range of circumduction', '兩邊同時，手肘彎曲90度，上臂與身體平行（shoulder 0度），手心向下|Sitting with arm at side elbow flexed to 90°, and forearm pronated', '0 無法做出動作|0 Noactivemotion', '1 部分動作，或可達到最大角度但過程不順暢|1 Partialmotion', '2 可來回達到最大角度且動作順暢|2 Complete motion with smoothness', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (21, 1, 3, -0.075, -1.664, 1.664, null, 'Finger mass extension', 'Finger mass extension', '請用力張開手掌，手指伸直<br>*註一：如必要，評估者可稍微支撐患側前臂：距離手腕約兩指幅處|Fully extend all fingers', '手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法執行|0 Noactivemotion', '1 部分動作|1 Partialmotion', '2 可完成動作至最大角度|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (22, 1, 3, 2.298, 0.574, -0.574, null, 'Grasp: Hook', 'Grasp: Hook', '請張開手掌，手指前二節慢慢彎曲，變成爪型。請出力撐住，不要被我扳開<br>*註一：個案須保持掌指關節(metacarpophalangeal joint)伸直，若個案無法伸直掌指關節，則0分<br>*註二：如必要，評估者可稍支撐患側前臂：距離手腕約2指幅處|Pretend you are holding the handle of a briefcase. Test this grip against resistance.', '手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法做到動作|0 Noactivemotion', '1 部分動作，或可完成但無法抗阻力|1 Graspisweak', '2 可做出動作亦能抗阻力|2 Grasp can be maintained against relatively great resistance', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (23, 1, 3, 2.116, -0.726, 0.726, null, 'Grasp: Lateral pinch', 'Grasp: Lateral pinch', '請張開手掌，大拇指慢慢靠向食指側邊併攏，我會在拇指與食指中間放一張紙。請用力夾住，紙不要被我拉走<br>*註一：個案大拇指指節不能彎曲；以大拇指側邊夾緊的方式，而不是以指腹捏紙<br>*註二：如必要，評估者可稍支撐患側前臂：距離手腕約2指幅處|Perform pure thumb adduction with the scrap of paper interposed between the thumb and first digit. Test this grip against resistance by asking the patient to hold as you attempt to pull the aper out with a slight tug.', '手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法做到動作|0 Noactivemotion', '1 部分動作，或可完成動作但無法夾住紙張|1 Thumbandindexfingercanbekeptinplacebutnotagainstaslighttug', '2 可完成動作且紙張不被拉走|2 Paper is held firmly against a tug', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (24, 1, 3, 2.826, -0.923, 0.923, null, 'Grasp: Palmar pinch', 'Grasp: Palmar pinch', '請張開手掌，將你的大拇指指腹靠近食指指腹，並夾住這支筆。請用力夾住，筆不要被我抽走<br>*註一：如必要，評估者可稍支撐患側前臂：距離手腕約2指幅處|Grasp a pen by opposing the thumb and index finger pads around the pen. Test this grip against resistance by asking the patient to hold as you attempt to pull the pencil out with a slight tug.', '手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法做到動作|1Noactivemotion', '1 部分動作，或可完成動作但無法夾住筆|2Thumbandindexfingercanbekeptinplacebutnotagainstaslighttug', '2 可完成動作且筆不被抽走|2 Paper is held firmly against a tug', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (25, 1, 3, 1.93, -1.326, 1.326, null, 'Grasp: Cylindrical grasp', 'Grasp: Cylindrical grasp', '請張開手掌，慢慢握住這個柱狀的瓶子。請出力握住，瓶子不要被我拉走<br>*註一：個案手掌張開的幅度須大於瓶子，直接將瓶子塞入手中則0分<br>*註二：如必要，評估者可稍支撐患側前臂：距離手腕約2指幅處|Grasp a small can (placed upright on a table without stabilization) by opening the fingers and opposing the volar surfaces of the thumb and digits. Test this grip against resistance by asking the patient to hold as you attempt to pull the can out with a slight tug. ', '手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法做到動作|2Noactivemotion', '1 部分動作，或可完成動作但無法握好瓶子|3Thumbandindexfingercanbekeptinplacebutnotagainstaslighttug', '2 可完成動作且瓶子不被拉走|2 Paper is held firmly against a tug', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (26, 1, 3, 2.207, -1.051, 1.051, null, 'Grasp: Spherical grasp', 'Grasp: Spherical grasp', '請張開手掌，慢慢地握住這顆球。請出力撐住，球不要被我拉走<br>*註一：球體可選網球，大小較為適中<br>*註二：如必要，評估者可稍支撐患側前臂：距離手腕約2指幅處|Grasp a tennis ball. Test this grip against resistance by asking the patient to hold as you attempt to pull the ball out with a slight tug.', '手肘彎曲90度，上臂與身體平行（shoulder 0度），前臂在正中姿勢|Sitting with arm on bedside table or lap, finger flexion (this may be attained passively if necessary)', '0 無法做到動作|3Noactivemotion', '1 部分動作，或可完成動作但無法握緊球|4Thumbandindexfingercanbekeptinplacebutnotagainstaslighttug', '2 可完成動作且球不被拉走|2 Paper is held firmly against a tug', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (27, 2, 3, -2.656, -2.48, 2.48, null, 'Flexor synergy：Hip flexion, supine', 'Flexor synergy：Hip flexion, supine', '請彎曲你的左/右腳(患側腳)，膝蓋頭去碰肚子，腳踝往上翹到最大|Flex hip, knee, and ankle as much as you can do', '個案平躺於治療床上|Start with leg fully extended at hip, knee, and ankle', '0 無法執行|0 Noactivemotion', '1 部分動作，或動作與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (28, 2, 3, -2.51, -1.779, 1.779, null, 'Flexor synergy：Knee flexion, supine', 'Flexor synergy：Knee flexion, supine', '請彎曲你的左/右腳(患側腳)，膝蓋頭去碰肚子，腳踝往上翹到最大|Flex hip, knee, and ankle as much as you can do', '個案平躺於治療床上|Start with leg fully extended at hip, knee, and ankle', '0 無法執行|0 Noactivemotion', '1 部分動作，或動作與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (29, 2, 3, -2.858, -1.581, 1.581, null, 'Extensor synergy：Hip extension, supine', 'Extensor synergy：Hip extension, supine', '請盡量同時伸直髖、膝、踝關節至最大角度，並抵抗我給你的阻力<br>*註一：若一開始稍微給阻力，個案即出現快速踢腳的動作，則為0分|Push your foot down and kick down and back', '個案平躺於治療床上，保持Flexor synergy 的正常結束位置(彎曲你的患側腳，膝蓋頭去碰肚子，腳踝往上翹到最大)，若個案無法自己做到 end range，由評估者先擺位好|Start in 90 degrees hip flexion, 90 degrees knee flexion and ankle dorsiflexion', '0 無法執行|0 Noactivemotion', '1 部分動作，或可完成動作但品質與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (30, 2, 3, -2.527, -1.687, 1.687, null, 'Extensor synergy：Hip adduction, supine', 'Extensor synergy：Hip adduction, supine', '請盡量同時伸直髖、膝、踝關節至最大角度，並抵抗我給你的阻力|Push your foot down and kick down and back', '個案平躺於治療床上，保持Flexor synergy 的正常結束位置(彎曲你的患側腳，膝蓋頭去碰肚子，腳踝往上翹到最大)，若個案無法自己做到 end range，由評估者先擺位好|Start in 90 degrees hip flexion, 90 degrees knee flexion and ankle dorsiflexion', '0 無法執行|1Noactivemotion', '1 部分動作，或可完成動作但品質與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (31, 2, 3, -2.722, -1.568, 1.568, null, 'Extensor synergy：Knee extension, supine', 'Extensor synergy：Knee extension, supine', '請盡量同時伸直髖、膝、踝關節至最大角度，並抵抗我給你的阻力<br>*註一：若一開始稍微給阻力，個案即出現快速踢腳的動作，則為2分|Push your foot down and kick down and back', '個案平躺於治療床上，保持Flexor synergy 的正常結束位置(彎曲你的患側腳，膝蓋頭去碰肚子，腳踝往上翹到最大)，若個案無法自己做到 end range，由評估者先擺位好|Start in 90 degrees hip flexion, 90 degrees knee flexion and ankle dorsiflexion', '0 無法執行|2Noactivemotion', '1 部分動作，或可完成動作但品質與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (32, 2, 3, -1.065, -1.098, 1.098, null, 'Extensor synergy：Ankle plantar flexion, supine', 'Extensor synergy：Ankle plantar flexion, supine', '請盡量同時伸直髖、膝、踝關節至最大角度，並抵抗我給你的阻力<br>*註一：若一開始稍微給阻力，個案即出現快速踢腳的動作，則為3分|Push your foot down and kick down and back', '個案平躺於治療床上，保持Flexor synergy 的正常結束位置(彎曲你的患側腳，膝蓋頭去碰肚子，腳踝往上翹到最大)，若個案無法自己做到 end range，由評估者先擺位好|Start in 90 degrees hip flexion, 90 degrees knee flexion and ankle dorsiflexion', '0 無法執行|3Noactivemotion', '1 部分動作，或可完成動作但品質與正常有明顯差異|1 Partialmotion', '2 可完成動作且品質與正常無異|2 Full motion', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (33, 2, 3, 0.4, -0.759, 0.759, null, 'Knee flexion, sitting', 'Knee flexion, sitting', '請將你的左/右腳(患側腳)向後縮到最裡面<br>*註一：膝蓋彎曲之判定標準，可以依患側腳跟是否超過健側腳跟判斷|Pull your heel back and under the chair', '請個案坐於有固定靠背的椅子，評估者確保個案軀幹穩定。健側腳膝蓋彎曲90度，腳板全部踩穩地面。患側腳膝蓋稍微朝前伸直，腳跟約在健側腳腳趾處|Affected knee is flexed 60° and unaffected knee is flexed 90°.', '0 沒有動作|0 Noactivemotion', '1 ≦90度|1 ≦90°', '2 > 90度|2 > 90°', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (34, 2, 3, 2.033, -0.954, 0.954, null, 'Ankle dorsiflexion, sitting', 'Ankle dorsiflexion, sitting', '請將你的兩腳腳板同時翹高|Keep your heel on the floor and lift your foot.', '請個案坐於有固定靠背的椅子，評估者確保個案軀幹穩定。兩腳膝蓋皆彎曲90度，腳板全部踩穩地面|Affected knee is flexed 60° and unaffected knee is flexed 90°.', '0 沒有動作|0 Noactivemotion', '1 部分動作，患側腳翹高的角度小於健側腳|1 Incompleteactiveflexion', '2 二腳翹高的角度差不多|2 Normal dorsiflexion (comparing to the unaffected side)', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (35, 2, 3, 3.455, -0.498, 0.498, null, 'Knee flexion to 90°, standing', 'Knee flexion to 90°, standing', '請你在髖關節不動的情況化，將左/右腳(患側腳)膝蓋彎曲超過90度，像踢屁股那樣<br>*註一 :個案一動作就出現抬大腿(hip flexion)，則0分|Keep your hip back, kick your bottom with your heel', '請個案雙腳併攏站立，手可扶在穩固的桌面，以確保平衡|', '0 沒有動作|0 Noactivemotion', '1 ≦90度|1 ≦90°', '2 > 90度|2 > 90°', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (36, 2, 3, 3.992, -0.587, 0.587, null, 'Ankle dorsiflexion, standing', 'Ankle dorsiflexion, standing', '請將你的兩腳腳板同時翹高|Keep your knee extended and your heel on the floor, lift your foot', '請個案雙腳併攏站立，手可扶在穩固的桌面，以確保平衡|', '0 沒有動作|0 Noactivemotion', '1 部分動作，患側腳翹高的角度小於健側腳|1 Partialmotion', '2 二腳翹高的角度差不多|2 Normal dorsiflexion (comparing to the unaffected side)', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (37, 2, 3, 4.458, -1.009, 1.009, null, 'Heel to opposite knee, (5 repetitions) -time', 'Heel to opposite knee, (5 repetitions) -time', '請將你的左/右腳(健側腳)，碰到另一腳腳踝，沿著小腿碰到膝蓋，再回到腳踝的位置，共來回5次，越快越好。左/右腳(健側腳)做完之後，換左/右腳(患側腳)做<br>*註一：個案患側腳腳踝無法沿著健側小腿滑動，則0分|"Bring your heel from your opposite ankle to your opposite knee, repeat 5 times, keeping your heel on your shin bone, move as fast as possible', '個案平躺於治療床上|Sitting with eyes open, performing movement with unaffected side first', '0 較健側多6秒|0 Activityismorethan6secondslongerthanunaffectedleg', '1 較健側多2～5秒|1 2～5secondslongerthanunaffectedleg1 2～5secondslongerthanunaffectedleg', '2 兩側差別少於2秒|2 Less than 2 seconds difference', '');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (38, 3, 4, -2.486, -0.217, -0.429, 0.645, 'Sitting without support', ' 無扶持下坐立', '腳須踩在地板上|Sit on the edge of an 50-cm-high examination table (a Bobath plane, for instance) with the feet touching the floor', null, '0 無法坐立|0 Cannot sit', '1 需些微扶持下始能坐立，如他人以單手幫忙|1 Can sit with slight support, for example, by one hand', '2 沒有扶持下，可以坐立超過10秒鐘|2 Can sit for more than 10 seconds without support', '3 沒有扶持下，可以坐立超過5分鐘|3 Can sit for 5 minutes without support');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (39, 3, 4, -1.333, -0.351, -0.209, 0.56, 'Standing with support', ' 扶持下站立', '不論腳的擺位是否良好，只要站穩即可。且無 orthosis等輔具|Feet position free, no other constrains, no orthosis or assistive devices', null, '0 扶持下仍無法站立|0 Cannot stand, even with support', '1 兩人用力扶持下，可站立|1 Can stand with strong support of 2 people', '2 一人中度扶持下，可站立|2 Can stand with moderate support of 1 people', '3 單手扶持下，可站立|3 Can stand with support of only 1 hand');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (40, 3, 4, 0.892, -0.025, -0.823, 0.848, 'Standing without support', ' 無扶持下站立', '不論腳的擺位是否良好，只要站穩即可（不能用輔具）|Feet position free, no other constraints, no orthosis or assistive devices', null, '0 沒有扶持下無法站立|0 Cannot stand without support', '1 沒有扶持下可站立超過10秒鐘或身體明顯地偏向一側（明顯地以單腳承重）|1 Can stand without support for 10 seconds or leans heavily on one leg', '2 沒有扶持可站立超過1分鐘或身體些微不對稱|2 Can stand without support for 1 minute or stands slightly asymmetrically', '3 沒有扶持可站立超過1分鐘同時手臂可在超過肩膀的高度下活動（如手臂左右揮動）|3 Can stand without support for more than 1 minute and at the same time perform arm movements (e.g., wave arms ) above the shoulder level');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (41, 3, 4, 4.712, -1.003, 0.138, 0.865, 'Standing on unaffected leg', ' 健側腳站立', '單腳站立－健側腳，且不能用輔具|One-leg standin, no orthosis or assistive devices', null, '0 無法站立|0 Cannot stand without support', '1 站立數秒鐘|1 Can stand without support for a few seconds', '2 站立超過5秒鐘|2 Can stand without support for more than 5 seconds', '3 站立超過10秒鐘|3 Can stand without support formore than 10 seconds');
insert into item (id, dimension, categorynumber, delta, step1, step2, step3, engtitle, chttitle, introduction, startpose, option1, option2, option3, option4) values (42, 3, 4, 5.906, -0.006, -0.362, 0.368, 'Standing on paretic leg', ' 患側腳站立', '單腳站立－患側腳，且不能用輔具|One-leg standin, no orthosis or assistive devices', null, '0 無法站立|0 Cannot stand without support', '1 站立≦ 5秒鐘|1 Can stand without support for a few seconds', '2 站立超過5秒鐘|2 Can stand without support for more than 5 seconds', '3 站立超過10秒鐘|3 Can stand without support formore than 10 seconds');
